<script type="text/javascript">
  $(document).ready(function() {
    $('#form-field-field_f5704df').change(function() {
      uploadFile();
      $(this).parent().append('<span id="messageBox" style="color:red;">Processing your request...</span>');
    });
  });

  function uploadFile() {
    var file = $('#form-field-field_f5704df')[0].files[0];
    var reader = new FileReader();

    reader.onload = function(event) {
      var arrayBuffer = event.target.result;

      $.ajax({
        url: 'https://skyai-document-ai.cognitiveservices.azure.com/formrecognizer/documentModels/prebuilt-businessCard:analyze?api-version=2022-08-31',
        type: 'POST',
        data: arrayBuffer,
        processData: false,
        contentType: 'application/octet-stream',
        headers: {
          'Ocp-Apim-Subscription-Key': '2d6131eb1ccc49c2a4741fad1f329236'
        },
        success: function(response, textStatus, jqXHR) {
          const responseCode = jqXHR.status;
          if (responseCode === 202) {
            const resultsUrl = jqXHR.getResponseHeader('Operation-Location');
            const resultsId = jqXHR.getResponseHeader('apim-request-id');

            setTimeout(function() {
              $.ajax({
                url: resultsUrl,
                contentType: 'application/json',
                headers: {
                  'Ocp-Apim-Subscription-Key': '2d6131eb1ccc49c2a4741fad1f329236'
                },
                success: function(response) {
                  if (response.status == "succeeded") {
                    const fields = response.analyzeResult.documents[0].fields;
                    // console.log(fields);
                    const {
                      ContactNames,
                      CompanyNames,
                      Websites,
                      JobTitles,
                      Emails,
                      MobilePhones
                    } = fields;

                    const nameDetails = ContactNames.valueArray[0].valueObject;
                    const fullName = nameDetails.FirstName.content + ' ' + nameDetails.LastName.content;
                    $('#form-field-name').val(fullName);

                    const companyName = parseData(CompanyNames.valueArray);
                    $('#form-field-field_7cfee85').val(companyName);

                    const jobTitle = parseData(JobTitles.valueArray);
                    $('#form-field-field_efd03d5').val(jobTitle);

                    const website = parseData(Websites.valueArray);
                    $('#form-field-field_fde0f34').val(website);

                    const email = parseData(Emails.valueArray);
                    $('#form-field-email').val(email);

                    const phone = parseData(MobilePhones.valueArray);
                    const filteredPhone = phone.replace(/[^0-9-*#]/gi, "");
                    $('#form-field-field_282c818').val(filteredPhone);
                    $('#messageBox').attr('style', 'color: green;').html('Your request is successful.');
                  } else {
                    $('#messageBox').html('Your request has been failed. Please try again.');
                  }
                }
              });
            }, 5000);
          }
        },
        error: function(jqXHR, textStatus, errorThrown) {
          console.log('File upload failed');
          console.log(textStatus, errorThrown);
        }
      });
    };

    reader.readAsArrayBuffer(file);
  }

  function parseData(contentArray) {
    const valueKey = contentArray.length > 1 ? contentArray.length - 1 : 0;
    const dataObject = contentArray[valueKey];

    if ($.inArray(dataObject.type, ["address", "string", "phoneNumber"])) {
      let confidence = 0;
      let currentContent = {};
      $.each(contentArray, function(index, content) {
        if (content.confidence > confidence) {
          currentContent = content;
          confidence = content.confidence;
        }
      });

      return currentContent.content;
    }
  }
</script>